import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import "./Results.css";

function Results(props) {
  return (
    <div className="main-container">
      <h1>Results</h1>

      <p>Congraduations! You made it to the end of the game</p>

      <p>Your Score is: {props.score}0% </p>

      <p>If you would like to play again, click on button Below</p>

      <button>
        <Link to={`/Game/${props.match.params.myVar}`}>PLAY AGAIN</Link>
      </button>
    </div>
  );
}

export default Results;
