import React from "react";
import { Link } from "react-router-dom";
import "./Home.css";
import useSound from "use-sound";
// import basketball from "https://www.youtube.com/watch?v=NsIB3-t2j3A";

function Home() {
  // const [play] = useSound(basketball);

  return (
    <div className="main-container">
      <style>
        @import
        url('https://fonts.googleapis.com/css2?family=Black+Han+Sans&display=swap');
      </style>
      <h1 className="app-name">NBA Trivia</h1>

      <button>
        <Link to="/Menu">Enter Game</Link>
      </button>

      <p>
        Do you think you're an NBA stats expert? You do? Well lets put that to a
        test!
      </p>

      <p>There are five categories of stats that you can test yourself on.</p>
      <p>There are over 400 players account for when playing the gam </p>

      <img src="https://media.giphy.com/media/12Tob44HkDJty8/giphy.gif" />
      <img src="https://media.giphy.com/media/3oEdv5e5Zd2gsczAhG/giphy.gif" />
      <img src="https://media.giphy.com/media/l0IxZLrBjtBafTOHm/giphy.gif" />
      <img src="https://media.giphy.com/media/F3tPNVyStwEq4/giphy.gif" />

      {/* <button onClick={play}>Motivate</button> */}
    </div>
  );
}

export default Home;
