import React, { useState, useEffect, useParams } from "react";
import { Route, Link } from "react-router-dom";
import Header from "./Header";
import Home from "./Home";
import GameMenu from "./GameMenu";
import Game from "./Game";

import axios from "axios";

function App() {
  const [playerInfo, setPlayerInfo] = useState([]);
  const [playerGif, setPlayerGif] = useState("");
  const params = useParams;

  const apiCallStats = async () => {
    const data = await axios.get(
      "https://www.balldontlie.io/api/v1/season_averages?player_ids[]=450"
    );
    console.log(data.data.data[0].pts);
    setPlayerInfo(data.data.data[0].pts);
  };

  const apiCallGifs = async () => {
    const data = await axios.get(
      "https://api.giphy.com/v1/gifs/search?api_key=cLVkasFAvpiN8CTvAkRlGkoBTskbN71s&q=lebron&limit=25&offset=0&rating=G&lang=en"
    );

    console.log(data);
  };

  useEffect(() => {
    apiCallStats();
    apiCallGifs();
  }, []);

  return (
    <>
      <header>
        <h1>NBA Trivia</h1>
      </header>
      <main>
        <Link to="/">Home</Link>
        <Route path="/" exact>
          <Home />
        </Route>
        <Route path="/Menu">
          <GameMenu />
        </Route>
        <Route path="/Game">
          <Game pts={playerInfo} />
        </Route>
      </main>
    </>
  );
}

export default App;
